"use strict";

var pool = require("../../config/database");
module.exports = {
  gettour: function gettour(callBack) {
    pool.query("select * from tour LEFT JOIN giamgiathem on tour.id_giamgiathem = giamgiathem.id_giamgiathem LIMIT 8 ", [], function (error, results) {
      if (error) {
        return callBack(error);
      }
      return callBack(null, results);
    });
  },
  gettour2: function gettour2(data, callBack) {
    if (data.LoaiTour !== null) {
      pool.query("select * from tour LEFT JOIN giamgiathem on tour.id_giamgiathem = giamgiathem.id_giamgiathem where tour.LoaiTour = ? ", [data.LoaiTour], function (error, results) {
        if (error) {
          return callBack(error);
        }
        return callBack(null, results);
      });
    } else {
      pool.query("select * from tour LEFT JOIN giamgiathem on tour.id_giamgiathem = giamgiathem.id_giamgiathem ", [], function (error, results) {
        if (error) {
          return callBack(error);
        }
        return callBack(null, results);
      });
    }
  },
  gettourid: function gettourid(id, callBack) {
    pool.query("select * from tour where tour.MaTour=?", [id], function (error, results) {
      if (error) {
        return callBack(error);
      }
      return callBack(null, results);
    });
  },
  gettouridlist: function gettouridlist(id, callBack) {
    pool.query("select * from tour where tour.MaTour=? GROUP BY tour.MaTour", [id], function (error, results) {
      if (error) {
        return callBack(error);
      }
      return callBack(null, results);
