                });
              }
              return res.status(200).json({
                data:"success",
              });
            });
          }
          else{
            return res.status(200).json({
              data:"exist",
            });
          }
          
        }
      );
    }
   
  },
  Gettourlove: (reg, res) => {
    const data={
      MaKH:reg.body.MaKH
    }
    gettourlove(data,(err, results) => {
      if (err) {
       
        return res.status(500).json({
          success: err,
          message: "Database connection error",
        });
      } 
      return res.status(200).json({
        data: results,
      });
    });
  },
};

