      if (error) {
        return callBack(error);
      }
      return callBack(null, results);
    });
  },
  gettouridlist: function gettouridlist(id, callBack) {
    pool.query("select * from tour where tour.MaTour=? GROUP BY tour.MaTour", [id], function (error, results) {
      if (error) {
        return callBack(error);
      }
      return callBack(null, results);
    });
  },
  gettouridbill: function gettouridbill(data, callBack) {
    pool.query("select * from tour where tour.MaTour=? and tour.NgayDi=?", [data.MaTour, data.NgayDi], function (error, results) {
      if (error) {
        return callBack(error);
      }
      return callBack(null, results);
    });
  },
  Searchtour: function Searchtour(data, callBack) {
    pool.query("select * from tour where tour.DiaDiemDen=? and tour.NgayDi>=? and tour.NgayVe<=? ", [data.DiaDiemDen, data.NgayDi, data.NgayVe], function (error, results) {
      if (error) {
        return callBack(error);
      }
      return callBack(null, results);
