    pool.query("SELECT tour.MaTour,tour.DiaDiemDi,tour.TenTour,tour.LoaiTour,tour.NgayDi,tour.GiaTour,GROUP_CONCAT(giamgia.ten_dotgiamgia SEPARATOR ' + ') AS ten_dotgiamgia, SUM(giamgia.mucgiamgia) AS mucgiamgia\n      FROM tour\n      INNER JOIN giamgia on tour.MaTour = giamgia.MaTour\n      WHERE tour.DiaDiemDi = ? AND tour.DiaDiemDen = ?\n      GROUP BY tour.NgayDi", [data.DiaDiemDi, data.DiaDiemDen], function (error, results) {
      if (error) {
        return callBack(error);
      }
      return callBack(null, results);
    });
  },
  Addtourlove: function Addtourlove(data, callBack) {
    pool.query("INSERT INTO touryeuthich (MaKH,MaTour) VALUES (?,?)", [data.MaKH, data.MaTour], function (error, results) {
      if (error) {
        return callBack(error);
      }
      return callBack(null, results);
