  static getphieu=
  "SELECT * from phieudattour INNER JOIN hoadon on phieudattour.MaPhieu=hoadon.MaPhieu WHERE phieudattour.MaTour=? and phieudattour.MaKH=?"
}

module.exports = BillModel;

